mapper.select:
SELECT * FROM (SELECT ROWNUM RNUM, TM.* FROM(SELECT * FROM
MESSAGEREV WHERE MESSAGE_REV = #{message_rev} and STORE_STATUS_YN = 'N' ORDER BY MESSAGE_NO DESC) TM) WHERE
RNUM BETWEEN #{startNo} and #{endNo}

SELECT * FROM (SELECT ROWNUM RNUM, TM.* FROM(SELECT * FROM
MESSAGESEN WHERE MESSAGE_SEN = #{message_sen} ORDER BY MESSAGE_NO DESC) TM) WHERE
RNUM BETWEEN #{startNo} and #{endNo}

SELECT * FROM (SELECT ROWNUM RNUM, TM.* FROM(SELECT * FROM
MESSAGEREV WHERE MESSAGE_REV = #{message_rev} and STORE_STATUS_YN ='Y' ORDER BY MESSAGE_NO DESC) TM) WHERE
RNUM BETWEEN #{startNo} and #{endNo}
