mapper.select:
select * from( select rownum rNum, Tb.* from (select * from
board 
where BOARD_KIND=#{board_kind} 
ORDER BY create_date DESC) Tb) 
where rNum between #{startNo} and #{endNo}

SELECT * FROM ( SELECT rownum rNum, Tb.* FROM (SELECT * FROM board JOIN MEMBER USING (MEMBER_NO)
WHERE BOARD_KIND = #{board_kind}

	AND BOARD_STATUS = 'Y'

ORDER BY create_date DESC
) Tb)WHERE rNum BETWEEN #{startNo} AND #{endNo}

SELECT * FROM ( SELECT rownum rNum, Tb.* FROM ( SELECT * FROM board WHERE 1=1 AND BOARD_STATUS = 'Y'
AND BOARD_KIND = #{board_kind}
AND board_type IN
#{type}
AND (board_content LIKE '%' || #{searchText} || '%' OR board_title LIKE '%' || #{searchText} || '%')
ORDER BY create_date DESC
ORDER BY board_hit DESC
ORDER BY board_like DESC
ORDER BY create_date DESC
) Tb ) WHERE rNum BETWEEN #{startNo} AND #{endNo}

SELECT COUNT(*) FROM board WHERE 1=1 AND BOARD_STATUS = 'Y'
AND BOARD_KIND = #{board_kind}
AND board_type IN
#{type}
AND board_content LIKE '%' || #{searchText} || '%'

SELECT COUNT(*) FROM board WHERE 1=1 AND BOARD_STATUS = 'Y'
AND BOARD_KIND = #{board_kind}
AND board_continent IN
#{location}
AND board_content LIKE '%' || #{searchText} || '%'

SELECT * FROM ( SELECT rownum rNum, Tb.* FROM ( SELECT * FROM board WHERE 1=1 AND BOARD_STATUS = 'Y'
AND BOARD_KIND = #{board_kind}
AND board_continent IN
#{location}
AND (board_content LIKE '%' || #{searchText} || '%' OR board_title LIKE '%' || #{searchText} || '%')
ORDER BY create_date DESC
ORDER BY board_hit DESC
ORDER BY board_like DESC
ORDER BY create_date DESC
) Tb ) WHERE rNum BETWEEN #{startNo} AND #{endNo}

choose.when:
UPDATE BOARD SET BOARD_TITLE = #{board_title},BOARD_TYPE=#{board_type},
BOARD_CONTENT = #{board_content},BOARD_IMG = #{board_img},BOARD_KIND=#{board_kind}
WHERE BOARD_NO = #{board_no}

choose.otherwise:
UPDATE BOARD SET BOARD_TITLE = #{board_title},BOARD_CONTENT = #{board_content},
BOARD_IMG = #{board_img},BOARD_KIND = #{board_kind},BOARD_CONTINENT = #{board_continent}
WHERE BOARD_NO = #{board_no}

